mapper.select:
SELECT *
FROM `schedule`
ORDER BY eventDate, startTime;

SELECT S.*, E.title AS eventTitle FROM `schedule` S INNER JOIN `event` E ON S.eventId = E.id
E.title LIKE CONCAT('%', #{searchKeyword}, '%') OR S.eventDate LIKE CONCAT('%', #{searchKeyword}, '%') OR S.startTime LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY eventDate, startTime;
